mapper.update:
UPDATE

SET
GUESS_TYPE = #{GUESS_TYPE},
SETTLEMENT_FLAG = #{SETTLEMENT_FLAG},
SETTLEMENT_DATE = now(),
SETTLEMENT_GOLD = #{SETTLEMENT_GOLD}
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLL_ID = #{DOLL_ID}

mapper.select:
select
*
from
tb_doll_guess_detail
where
GUESS_KEY  = #{GUESS_KEY}
AND
PLAYBACK_ID = #{PLAYBACK_ID}
AND
DOLL_ID = #{DOLL_ID}

select
*
from
tb_doll_guess_detail
where
GUESS_KEY  = #{GUESS_KEY}
AND
PLAYBACK_ID = #{PLAYBACK_ID}
AND
DOLL_ID = #{DOLL_ID}

SELECT
*
FROM
tb_doll_guess_detail
WHERE
PLAYBACK_ID = #{PLAYBACK_ID}
and
APP_USER_ID = #{APP_USER_ID}
AND
DOLL_ID = #{DOLL_ID}

SELECT * from TB_DOLL_GUESS_DETAIL WHERE
GUESS_KEY  = #{GUESS_KEY}
AND
PLAYBACK_ID = #{PLAYBACK_ID}
AND
DOLL_ID = #{DOLL_ID}

SELECT * from TB_DOLL_GUESS_DETAIL WHERE
GUESS_KEY  != #{GUESS_KEY}
AND
PLAYBACK_ID = #{PLAYBACK_ID}
AND
DOLL_ID = #{DOLL_ID}
