mapper.select:
SELECT * FROM
TB_DOLL_PLAY_DETAIL
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLLID = #{DOLLID}

SELECT * FROM
TB_DOLL_PLAY_DETAIL
WHERE
GUESS_ID = #{GUESS_ID}
and
DOLLID = #{DOLLID}
and
USERID = #{USERID}

mapper.update:
UPDATE
TB_DOLL_PLAY_DETAIL
SET
CAMERA_DATE = #{CAMERA_DATE}
WHERE
ID = #{ID}

UPDATE
tb_doll_play_detail
SET
POST_STATE = #{POST_STATE}
WHERE
ID = #{ID}
